{
  "title": "Docker containers",
  "type": "table",
  "params": {
    "perPage": 8,
    "showMeticsAtAllLevels": false,
    "showPartialRows": false,
    "showTotal": true,
    "sort": {
      "columnIndex": null,
      "direction": null
    },
    "totalFunc": "sum"
  },
  "aggs": [
    {
      "id": "2",
      "enabled": true,
      "type": "terms",
      "schema": "bucket",
      "params": {
        "field": "docker.container.name",
        "size": 5,
        "order": "desc",
        "orderBy": "1",
        "customLabel": "Name"
      }
    },
    {
      "id": "3",
      "enabled": true,
      "type": "max",
      "schema": "metric",
      "params": {
        "field": "docker.cpu.total.pct",
        "customLabel": "CPU usage (%)"
      }
    },
    {
      "id": "4",
      "enabled": true,
      "type": "max",
      "schema": "metric",
      "params": {
        "field": "docker.diskio.total",
        "customLabel": "DiskIO"
      }
    },
    {
      "id": "5",
      "enabled": true,
      "type": "max",
      "schema": "metric",
      "params": {
        "field": "docker.memory.usage.pct",
        "customLabel": "Mem (%)"
      }
    },
    {
      "id": "6",
      "enabled": true,
      "type": "max",
      "schema": "metric",
      "params": {
        "field": "docker.memory.rss.total",
        "customLabel": "Mem RSS"
      }
    },
    {
      "id": "1",
      "enabled": true,
      "type": "cardinality",
      "schema": "metric",
      "params": {
        "field": "docker.container.id",
        "customLabel": "Number of Containers"
      }
    }
  ],
  "listeners": {}
}

{
  "title": "Docker CPU usage",
  "type": "area",
  "params": {
    "addLegend": true,
    "addTimeMarker": false,
    "addTooltip": true,
    "defaultYExtents": false,
    "interpolate": "linear",
    "legendPosition": "top",
    "mode": "stacked",
    "scale": "linear",
    "setYExtents": false,
    "shareYAxis": true,
    "smoothLines": true,
    "times": [],
    "yAxis": {}
  },
  "aggs": [
    {
      "id": "1",
      "enabled": true,
      "type": "percentiles",
      "schema": "metric",
      "params": {
        "field": "docker.cpu.total.pct",
        "percents": [
          75
        ],
        "customLabel": "Total CPU time"
      }
    },
    {
      "id": "2",
      "enabled": true,
      "type": "date_histogram",
      "schema": "segment",
      "params": {
        "field": "@timestamp",
        "interval": "auto",
        "customInterval": "2h",
        "min_doc_count": 1,
        "extended_bounds": {}
      }
    },
    {
      "id": "3",
      "enabled": true,
      "type": "terms",
      "schema": "group",
      "params": {
        "field": "docker.container.name",
        "size": 5,
        "order": "desc",
        "orderBy": "1.75",
        "customLabel": "Container name"
      }
    }
  ],
  "listeners": {}
}

{
  "title": "Top hosts by CPU usage",
  "type": "table",
  "params": {
    "perPage": 10,
    "showPartialRows": false,
    "showMeticsAtAllLevels": false,
    "sort": {
      "columnIndex": null,
      "direction": null
    },
    "showTotal": false,
    "totalFunc": "sum"
  },
  "aggs": [
    {
      "id": "1",
      "enabled": true,
      "type": "avg",
      "schema": "metric",
      "params": {
        "field": "system.cpu.user.pct",
        "customLabel": "Used memory"
      }
    },
    {
      "id": "2",
      "enabled": true,
      "type": "max",
      "schema": "metric",
      "params": {
        "field": "system.load.1",
        "customLabel": "Load last min"
      }
    },
    {
      "id": "3",
      "enabled": true,
      "type": "max",
      "schema": "metric",
      "params": {
        "field": "system.load.5",
        "customLabel": "Load last 5 mins"
      }
    },
    {
      "id": "4",
      "enabled": true,
      "type": "max",
      "schema": "metric",
      "params": {
        "field": "system.load.15",
        "customLabel": "Load last 15 mins"
      }
    },
    {
      "id": "5",
      "enabled": true,
      "type": "max",
      "schema": "metric",
      "params": {
        "field": "system.load.norm.1",
        "customLabel": "Load/cores last min"
      }
    },
    {
      "id": "6",
      "enabled": true,
      "type": "max",
      "schema": "metric",
      "params": {
        "field": "system.load.norm.5",
        "customLabel": "Load/cores last 5 mins"
      }
    },
    {
      "id": "7",
      "enabled": true,
      "type": "max",
      "schema": "metric",
      "params": {
        "field": "system.load.norm.15",
        "customLabel": "Load/cores last 15 mins"
      }
    },
    {
      "id": "8",
      "enabled": true,
      "type": "terms",
      "schema": "bucket",
      "params": {
        "field": "beat.name",
        "size": 50,
        "order": "desc",
        "orderBy": "1",
        "customLabel": "Host"
      }
    }
  ],
  "listeners": {}
}
